sci:arg.sci:value:
#Text := @Text;

if @'Left Icon' ne '' then
	#Text := '<i class="$(Left Icon) icon"></i> '. #Text;
end if;
if @'Right Icon' ne '' then
	#Text := #Text . '
	<i class="$(Right Icon) icon float right"></i>';
end if;
if @Detail ne '' then
	#Text := #Text . ' <div class="detail">$(Detail)</div>';
end if;

if @Tag is defined and @Tag then
	#tag := ' tag ';
else
	#tag := '';
end if;

if @horizontal is defined and @horizontal then
	#horizontal := ' horizontal ';
else
	#horizontal := '';
end if;
if @floating is defined and @floating then
	#floating := ' floating ';
else
	#floating := '';
end if;
if @Basic is defined and @Basic then
	#basic := ' basic ';
else
	#basic := '';
end if;
if @circular is defined and @circular then
	#circular := ' circular ';
else
	#circular := '';
end if;


@html_ := '
<div id="$(guid)" class="ui $(Corner) $(size) $(color)'.#tag.#horizontal.#floating.#basic.#circular.' $(Ribbon) $(Attached) label">'.#Text.'</div>';

#displayName := trim(Parameter('Expression', 2));
if(strlength(#displayName) > 0) then	
	/* Find the first line break */
	#nl := FindSubstring(#displayName, "\r");
	if #nl <= 0 then
		#nl := FindSubstring(#displayName, "\n");
	end if;

	/* Keep the first line only */
	if (#nl > 0) then
		StrErase(#displayName, #nl);
	end if;
	
	#displayName;
else
	"";
end if;

