sci:arg.sci:value:
#displayName := trim(Parameter('Expression', 2));
if(strlength(#displayName) > 0) then	
	/* Find the first line break */
	#nl := FindSubstring(#displayName, "\r");
	if #nl <= 0 then
		#nl := FindSubstring(#displayName, "\n");
	end if;

	/* Keep the first line only */
	if (#nl > 0) then
		StrErase(#displayName, #nl);
	end if;
	
	#displayName;
else
	"";
end if;

/* SVG is not allowed to execute JS in the HTML DOM if the servername is different 
	so lets replace PP 9 hostname synonyms with a relative link.
	To do: this currently breaks interactive SVG JavaScript links in an iFrame.
*/
if @'/pilot_settings/ROOT' is defined then
	if text like '%'.@'/pilot_settings/ROOT'.'%.svg">%' then
		text := strReplace(Text, @'/pilot_settings/ROOT', '');
	end if;
end if;

// Find relative image URLs and make them absolute

#rurls := rMatch(text, 'src="(.*?)"', 'igs');
rurls := #rurls;
resize(#ss, 0);

for #i in 1 .. numvalues(#rurls) loop
	#u := #rurls[#i];
	if findSubstring(#u, '//') == 0 then // relative
		append(#ss, strReplace(strReplace(#u, 'src="', ''), '"', ''));
	end if;
end loop;

#prefix := @ServerRoot.'/jobs/'.@username.'/'.@RunID;
for #i in 1 .. numvalues(#ss) loop
	#u := #ss[#i];
	if #u ne '' then
		if substring(#u, 1) eq '/' then
			// add http://server:port
			text := strReplace(text, #u, #prefix.urlencode(#u, true));
		else
			// add http://server:port/
			text := strReplace(text, #u, #prefix.'/'.urlencode(#u, true));
		end if;
	end if;
end loop;

#displayName := trim(Parameter('Expression', 2));
if(strlength(#displayName) > 0) then	
	/* Find the first line break */
	#nl := FindSubstring(#displayName, "\r");
	if #nl <= 0 then
		#nl := FindSubstring(#displayName, "\n");
	end if;

	/* Keep the first line only */
	if (#nl > 0) then
		StrErase(#displayName, #nl);
	end if;
	
	#displayName;
else
	"";
end if;

sci:arg.sci:enabledscript:
SetDisabledDetails(
	       'To enable '.ParameterName().', set CharacterEncoding to a non-ANSI value');
	Parameter('CharacterEncoding') ne 'ANSI';

sci:arg.sci:value:
